package quiz;

public class Quiz8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		float f = 2.0f;
		int sum = (int) (i + f);
		int sub = (int) (i - f);
		int multi = (int) (i * f);
		int div = (int) (i / f);
		System.out.println(sum + " " + sub + " " + multi + " " + div);
	}

}
